const TestimonialSection = () => {
  const cardRefs = useRef([]);

  useGSAP(() => {
    gsap.set(".testimonials-section", {
      marginTop: "-140vh",
    });

    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: ".testimonials-section",
        start: "top bottom",
        end: "200% top",
        scrub: true,
      },
    });

    tl.to(".testimonials-section .first-title", {
      xPercent: 70,
    })
      .to(
        ".testimonials-section .sec-title",
        {
          xPercent: 25,
        },
        "<"
      )
      .to(
        ".testimonials-section .third-title",
        {
          xPercent: -50,
        },
        "<"
      );

    const pinTl = gsap.timeline({
      scrollTrigger: {
        trigger: ".testimonials-section",
        start: "10% top",
        end: "200% top",
        scrub: 1.5,
        pin: true,
      },
    });

    pinTl.from(".skill-card", {
      yPercent: 150,
      stagger: 0.2,
      ease: "power1.inOut",
    });
  });

  return (
    <section className="testimonials-section">
      <div className="absolute size-full flex flex-col items-center pt-[5vw]">
        <h1 className="text-black first-title">My</h1>
        <h1 className="text-light-brown sec-title">Skills</h1>
      </div>

      <div className="pin-box">
        {skills.map((skill, index) => (
          <div
            key={index}
            className={`skill-card ${skill.translation || ""} ${skill.rotation}`}
            ref={(el) => (cardRefs.current[index] = el)}
          >
            <div className="size-full flex flex-col items-center justify-center p-4 bg-white rounded-lg shadow-lg">
              <div className="w-16 h-16 mb-2">
                {skill.icon}
              </div>
              <span className="text-lg font-medium text-gray-800">{skill.name}</span>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default TestimonialSection;